/// Videos in mp4 fomrat that should loop like GIFs.
/// All videos should be placed in assets/gifMP4s.
enum GifVideo: String, CaseIterable {
    case access
    case access_dark
    case app
    case app_dark
    case arrow_up
    case arrow_up_dark
    case blank_canvas
    case blank_canvas_dark
    case heart
    case heart_dark
    case id
    case id_dark
